import React, { useEffect, useState }  from 'react';

import { Link } from 'react-router-dom';
import '../styles/Navigation.css';



function Navigation() {
  const [logoAnimated, setLogoAnimated] = useState(false);

  useEffect(() => {
    setLogoAnimated(true);
  }, []);

  const [confettiProps, setConfettiProps] = useState({
    run: false,
    recycle: false,
    numberOfPieces: 200,
    initialVelocityY: { min: -15, max: -5 },
    initialVelocityX: { min: -10, max: 10 },
    gravity: 0.1,
    wind: 0,
    colors: ['#ff00ff', '#ff9900', '#ff0066', '#3300ff', '#00ff00', '#ffff00'],
    opacity: 1,
  });
  const { width, height } = useWindowSize();

  return (
    <nav>
      <header>
        <ul> 
          <li><Link to="/" className={`logo ${logoAnimated ? 'animate' : ''}`}>Repute</Link></li>
          <li><Link to="/contact">Home</Link></li>
          <li><Link to="/contact">Contact</Link></li>
          <li><Link to="/features">Features</Link></li>
          <li><Link to="/resources">Resources</Link></li>
          <li><Link to="/pricing">Pricing</Link></li>
          <li><Link to="/signup" className="get-started">Get started</Link></li>
        </ul>
      </header>
    </nav>
  );
}

export default Navigation;
