import React, { useEffect, useRef } from 'react';
import './shootingstars.css';

const ShootingStars = () => {
  const starRef = useRef(null);

  useEffect(() => {
    const animateStar = () => {
      if (starRef.current) {
        starRef.current.style.left = '-50px';
        starRef.current.style.top = `${Math.random() * 80 + 10}%`; // Avoid edges
        starRef.current.style.opacity = '1';

        setTimeout(() => {
          starRef.current.style.left = 'calc(100% + 50px)';

          // Schedule the next animation
          setTimeout(() => {
            starRef.current.style.opacity = '0';
            setTimeout(animateStar, Math.random() * 3000 + 1000); // Random delay between 1-4 seconds
          }, 3000); // Duration of star's visibility
        }, 100);
      }
    };

    animateStar();

    return () => {
      // Cleanup if needed
    };
  }, []);

  return (
    <div className="shooting-stars-container">
      <div ref={starRef} className="shooting-star">
        <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M12 2L14.4 9.6H22L16 14.4L18.4 22L12 17.2L5.6 22L8 14.4L2 9.6H9.6L12 2Z" fill="#FFFFD4"/>
        </svg>
      </div>
    </div>
  );
};

export default ShootingStars;
