import React, { useEffect, useState, useRef } from 'react';
import './App.css';
import { setupParticles } from './particles';
import NeonButton from './components/NeonButton';
import StarRating from './components/StarRating';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Home from './Home';
import Contact from './Contact';

function App() {
  const [neonColor, setNeonColor] = useState(getRandomNeonColor());
  const canvasRef = useRef(null);

  function getRandomNeonColor() {
    const neonColors = [
      '#ff00ff', '#ff9900', '#ff0066', '#3300ff'
    ];
    return neonColors[Math.floor(Math.random() * neonColors.length)];
  }

  function darkenColor(color, amount) {
    return '#' + color.replace(/^#/, '').replace(/../g, color => ('0' + Math.max(0, Math.min(255, parseInt(color, 16) - amount)).toString(16)).substr(-2));
  }

  useEffect(() => {
    if (canvasRef.current) {
      setupParticles(canvasRef.current);
    }
    const darkerNeonColor = darkenColor(neonColor, 20);
    document.documentElement.style.setProperty('--neon-color', darkerNeonColor);
  }, [neonColor]);

  useEffect(() => {
    const generateColor = () => {
      const hue = Math.floor(Math.random() * 360);
      const color = `hsl(${hue}, 85%, 40%)`; // Original bright color
      const lighterColor = `hsl(${hue}, 100%, 35%)`; // Lighter version for text (increased from 30% to 35%)
      document.documentElement.style.setProperty('--neon-color', color);
      document.documentElement.style.setProperty('--neon-text-color', lighterColor);
    };

    generateColor();
    // If you want to change color periodically:
    // const interval = setInterval(generateColor, 5000);
    // return () => {
    //   clearInterval(interval);
    // return () => clearInterval(interval);
  }, []);

  return (
    <Router>
      <div className="App">
        <canvas ref={canvasRef} id="particleCanvas"></canvas>
        <header>
          <nav>
            <Link to="/">Home</Link>
            <Link to="/contact">Contact</Link>
          </nav>
        </header>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>

        <footer style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100px'
        }}>
          <p style={{
            fontSize: 'calc(16px + 25px)',
            margin: 0
          }}>&copy; 2024 Repute. All rights reserved.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
