import React, { useEffect, useState, useRef } from 'react';
import { Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';
import './App.css';
import NeonButton from './components/NeonButton';
import StarRating from './components/StarRating';
import Contact from './components/Contact';
import Features from './components/Features';
import Testimonials from './components/Testimonials';
import Pricing from './components/Pricing';
import InteractiveTestimonial from './components/InteractiveTestimonial';
import SignUp from './components/SignUp';
import ThemeToggle from './components/ThemeToggle';
import { ThemeProvider } from './components/ThemeContext';
import ReactConfetti from 'react-confetti';
import { useWindowSize } from 'react-use';
import confetti from 'canvas-confetti';
import ShootingStars from './components/shootingstars';

function App() {
  const [neonColor, setNeonColor] = useState(getRandomNeonColor());
  const canvasRef = useRef(null);
  const featuresRef = useRef(null);
  const navigate = useNavigate();
  const location = useLocation();
  const [logoAnimated, setLogoAnimated] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [confettiProps, setConfettiProps] = useState({
    run: false,
    recycle: false,
    numberOfPieces: 200,
    initialVelocityY: { min: -15, max: -5 },
    initialVelocityX: { min: -10, max: 10 },
    gravity: 0.1,
    wind: 0,
    colors: ['#ff00ff', '#ff9900', '#ff0066', '#3300ff', '#00ff00', '#ffff00'],
    opacity: 1,
  });
  const { width, height } = useWindowSize();
  const [stars, setStars] = useState([]);

  function getRandomNeonColor() {
    const neonColors = [
      '#ff00ff', // Magenta
      '#ff9900', // Orange
      '#ff0066', // Pink
      '#3300ff'  // Blue
    ];
    return neonColors[Math.floor(Math.random() * neonColors.length)];
  }

  function darkenColor(color, amount) {
    return '#' + color.replace(/^#/, '').replace(/../g, color => ('0' + Math.max(0, Math.min(255, parseInt(color, 16) - amount)).toString(16)).substr(-2));
  }

  useEffect(() => {
    const darkerNeonColor = darkenColor(neonColor, 20);
    document.documentElement.style.setProperty('--neon-color', darkerNeonColor);
  }, [neonColor]);

  const scrollToFeatures = () => {
    if (location.pathname !== '/') {
      navigate('/', { state: { scrollToFeatures: true } });
    } else {
      featuresRef.current?.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    if (location.state?.scrollToFeatures) {
      featuresRef.current?.scrollIntoView({ behavior: 'smooth' });
      navigate(location.pathname, { replace: true, state: {} });
    }
  }, [location, navigate]);

  useEffect(() => {
    setLogoAnimated(true);
  }, []);

  useEffect(() => {
    if (location.pathname === '/pricing' && !showConfetti) {
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 5000); // Stop confetti after 5 seconds
    }
  }, [location.pathname, showConfetti]);

  useEffect(() => {
    if (location.pathname === '/pricing' && !confettiProps.run) {
      setConfettiProps(prev => ({ ...prev, run: true }));

      // Gradually reduce the number of pieces over 5 seconds
      const startTime = Date.now();
      const confettiInterval = setInterval(() => {
        const elapsedTime = Date.now() - startTime;
        if (elapsedTime >= 5000) {
          clearInterval(confettiInterval);
          setConfettiProps(prev => ({ ...prev, numberOfPieces: 0 }));
        } else {
          const remainingPieces = Math.max(0, 200 - Math.floor((elapsedTime / 5000) * 200));
          setConfettiProps(prev => ({ ...prev, numberOfPieces: remainingPieces }));
        }
      }, 100);
    }
  }, [location.pathname, confettiProps.run]);

  useEffect(() => {
    if (location.pathname === '/pricing' && !confettiProps.run) {
      setConfettiProps(prev => ({ ...prev, run: true }));

      // Start fading out after 5 seconds
      setTimeout(() => {
        const fadeInterval = setInterval(() => {
          setConfettiProps(prev => {
            const newOpacity = Math.max(0, prev.opacity - 0.05);
            if (newOpacity <= 0) {
              clearInterval(fadeInterval);
              return { ...prev, run: false, opacity: 0 };
            }
            return { ...prev, opacity: newOpacity };
          });
        }, 100); // Adjust every 100ms for smooth fading
      }, 5000);

      // Stop confetti after 7 seconds
      setTimeout(() => {
        setConfettiProps(prev => ({ ...prev, run: false, opacity: 0 }));
      }, 7000);
    }
  }, [location.pathname, confettiProps.run]);

  useEffect(() => {
    if (location.pathname === '/pricing') {
      launchConfetti();
    }
  }, [location.pathname]);

  function launchConfetti() {
    var duration = 4 * 1000; // 4 seconds
    var animationEnd = Date.now() + duration;
    var defaults = { startVelocity: 30, spread: 360, ticks: 60, zIndex: 0 };

    function randomInRange(min, max) {
      return Math.random() * (max - min) + min;
    }

    var interval = setInterval(function() {
      var timeLeft = animationEnd - Date.now();

      if (timeLeft <= 0) {
        return clearInterval(interval);
      }

      var particleCount = 50 * (timeLeft / duration);

      // Since confetti will be scattered across the entire screen, using random origin points
      confetti(Object.assign({}, defaults, {
        particleCount,
        origin: { x: randomInRange(0.1, 0.3), y: Math.random() - 0.2 }
      }));
      confetti(Object.assign({}, defaults, {
        particleCount,
        origin: { x: randomInRange(0.7, 0.9), y: Math.random() - 0.2 }
      }));
    }, 250);
  }

  useEffect(() => {
    const generateStars = () => {
      const newStars = Array.from({ length: 50 }, () => ({
        left: `${Math.random() * 100}%`,
        top: `${Math.random() * 100}%`,
        animationDelay: `${Math.random() * 5}s`
      }));
      setStars(newStars);
    };

    generateStars();
  }, []);

  return (
    <ThemeProvider>
      <div className="App">
        {location.pathname !== '/pricing' && (
          <>
            <ShootingStars />
            <div className="stars-background">
              {stars.map((star, index) => (
                <div
                  key={index}
                  className="star"
                  style={{
                    left: star.left,
                    top: star.top,
                    animationDelay: star.animationDelay
                  }}
                />
              ))}
            </div>
          </>
        )}
        {showConfetti && (
          <ReactConfetti
            width={width}
            height={height}
            recycle={false}
            numberOfPieces={200}
            initialVelocityY={{ min: -15, max: -5 }}
            initialVelocityX={{ min: -10, max: 10 }}
            gravity={0.1}
            colors={['#ff00ff', '#ff9900', '#ff0066', '#3300ff', '#00ff00', '#ffff00']}
            style={{
