import React, { useEffect, useState, useRef } from 'react';
import { Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';
import './App.css';
import NeonButton from './components/NeonButton';
import StarRating from './components/StarRating';
import Contact from './Contact';
import Features from './components/Features';
import Testimonials from './components/Testimonials';
import Pricing from './components/Pricing';  // Make sure this line is present
import InteractiveTestimonial from './components/InteractiveTestimonial';

function App() {
  const [neonColor, setNeonColor] = useState(getRandomNeonColor());
  const canvasRef = useRef(null);
  const featuresRef = useRef(null);
  const navigate = useNavigate();
  const location = useLocation();

  function getRandomNeonColor() {
    const neonColors = [
      '#ff00ff', // Magenta
      '#ff9900', // Orange
      '#ff0066', // Pink
      '#3300ff'  // Blue
    ];
    return neonColors[Math.floor(Math.random() * neonColors.length)];
  }

  function darkenColor(color, amount) {
    return '#' + color.replace(/^#/, '').replace(/../g, color => ('0' + Math.max(0, Math.min(255, parseInt(color, 16) - amount)).toString(16)).substr(-2));
  }

  useEffect(() => {
    const darkerNeonColor = darkenColor(neonColor, 20);
    document.documentElement.style.setProperty('--neon-color', darkerNeonColor);
  }, [neonColor]);

  const scrollToFeatures = () => {
    if (location.pathname !== '/') {
      navigate('/', { state: { scrollToFeatures: true } });
    } else {
      featuresRef.current?.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    if (location.state?.scrollToFeatures) {
      featuresRef.current?.scrollIntoView({ behavior: 'smooth' });
      navigate(location.pathname, { replace: true, state: {} });
    }
  }, [location, navigate]);

  return (
    <div className="App">
      <header>
        <a href="/" className="logo-link">
          <div className="logo animate-logo">Repute</div>
        </a>
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/contact">Contact</Link></li>
            <li><Link to="/features">Features</Link></li>
            <li><Link to="/resources">Resources</Link></li>
            <li><Link to="/pricing">Pricing</Link></li>
          </ul>
        </nav>
      </header>

      <Routes>
        <Route path="/" element={
          <main>
            <div className="hero">
              <div className="main-content">
                <h1 className="main-heading">
                  <div className="heading-line">Boost Trust with</div>
                  <div className="heading-line text-pink">AI-Crafted Testimonials</div>
                </h1>
                <p className="subtitle">Generate authentic testimonials in seconds with our AI-powered platform</p>
                <InteractiveTestimonial />
                <Features featuresRef={featuresRef} />
                <Testimonials />
              </div>
            </div>
          </main>
        } />
        <Route path="/contact" element={<Contact />} />
        <Route path="/pricing" element={<Pricing />} />
      </Routes>

      <footer style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100px'
      }}>
        <p style={{
          fontSize: 'calc(16px + 25px)',
          margin: 0
        }}>&copy; 2024 Repute. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
