import React, { useState, useEffect } from 'react';
import './InteractiveTestimonial.css';
import portrait1 from '../Assets/pexels-jonaorle-4029925.jpg';
import portrait2 from '../Assets/pexels-olly-774909.jpg';
import portrait3 from '../Assets/pexels-linkedin-2182970.jpg';
import portrait4 from '../Assets/pexels-jordan-bergendahl-2628960-10402659.jpg';

const portraits = [
  portrait1,
  portrait2,
  portrait3,
  portrait4,
  // Add more portraits here if needed
];

const testimonials = [
  "Absolutely amazing! The AI-generated testimonials have boosted our credibility and sales like never before. Highly recommend!",
  "This tool is a game-changer! I can't believe how quickly it generates authentic-sounding reviews. Our clients love it!",
  "We saw a 30% increase in conversions after implementing these AI testimonials. They add instant trust and authenticity to our website.",
  "I was skeptical at first, but the AI testimonials are spot on. They sound so genuine that even I couldn't tell they were generated.",
  "The best investment we've made this year. The testimonials are not only convincing but also beautifully presented on our site.",
  "We've tried multiple testimonial tools, but this one stands out. The AI's ability to generate contextually relevant reviews is unmatched.",
  "Our customers have been leaving better reviews since we started using these AI testimonials. It's like the tool knows exactly what to say!",
  "The ease of use and the quality of the testimonials have saved us so much time and effort. This tool is a must-have for any online business.",
  "Incredible technology! The AI testimonials have added so much value to our marketing strategy. Our clients trust us more now.",
  "The testimonials generated are so realistic that our team thought they were written by our actual customers. Truly impressive!"
];

const starRatings = [4, 5];

const InteractiveTestimonial = () => {
  const [portrait, setPortrait] = useState(portraits[0]);
  const [testimonial, setTestimonial] = useState('Click "Generate Testimonial" to see a testimonial');
  const [rating, setRating] = useState(0);
  const [isGenerating, setIsGenerating] = useState(false);
  const [isGlowing, setIsGlowing] = useState(false);
  const [hasGenerated, setHasGenerated] = useState(false);

  const generateTestimonial = () => {
    setIsGenerating(true);
    setIsGlowing(false); // Ensure glow is off when generating starts

    // Generate portrait
    let portraitInterval = setInterval(() => {
      setPortrait(portraits[Math.floor(Math.random() * portraits.length)]);
    }, 100);

    setTimeout(() => {
      clearInterval(portraitInterval);
      setPortrait(portraits[Math.floor(Math.random() * portraits.length)]);
    }, 1500);

    // Generate testimonial text
    setTimeout(() => {
      let testimonialInterval = setInterval(() => {
        setTestimonial(testimonials[Math.floor(Math.random() * testimonials.length)]);
      }, 100);

      setTimeout(() => {
        clearInterval(testimonialInterval);
        setTestimonial(testimonials[Math.floor(Math.random() * testimonials.length)]);
      }, 1500);
    }, 1000);

    // Generate star rating
    setTimeout(() => {
      let ratingInterval = setInterval(() => {
        setRating(starRatings[Math.floor(Math.random() * starRatings.length)]);
      }, 100);

      setTimeout(() => {
        clearInterval(ratingInterval);
        setRating(starRatings[Math.floor(Math.random() * starRatings.length)]);
        setIsGenerating(false);
        setIsGlowing(true); // Apply glow after generation
        setHasGenerated(true); // Mark that a testimonial has been generated
      }, 2000);
    }, 2000);
  };

  const generateStars = (rating) => {
    return Array(5).fill(0).map((_, index) => (
      <span key={index} className={`star ${index < rating ? 'filled' : ''}`}>
        {index < rating ? '⭐' : '☆'}
      </span>
    ));
  };

  return (
    <div className="interactive-testimonial">
      <button onClick={generateTestimonial} disabled={isGenerating}>
        Generate Testimonial
      </button>
      <div className="testimonial-wrapper">
        <div className={`testimonial-container ${isGenerating ? 'generating' : ''}`}>
          <div className="testimonial-portrait">
            <img src={portrait} alt="Portrait of a person" />
          </div>
          <div className={`testimonial-content ${isGlowing && hasGenerated ? 'glow' : ''}`}>
            <p>{testimonial}</p>
          </div>
        </div>
        <div className="testimonial-stars">
          {generateStars(rating)}
        </div>
      </div>
    </div>
  );
};

export default InteractiveTestimonial;
