import React, { useEffect, useRef } from 'react';
import './App.css';

function App() {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const particles = [];
    const particleCount = 50; // Now we'll use this

    class Particle {
      constructor() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.size = Math.random() * 3 + 1;
        this.speedX = Math.random() * 3 - 1.5;
        this.speedY = Math.random() * 3 - 1.5;
        this.color = '#00B896';
        this.trail = [];
        this.trailLength = 5;
      }

      update() {
        this.trail.unshift({ x: this.x, y: this.y });
        if (this.trail.length > this.trailLength) {
          this.trail.pop();
        }

        this.x += this.speedX;
        this.y += this.speedY;

        if (this.x > canvas.width) this.x = 0;
        else if (this.x < 0) this.x = canvas.width;
        if (this.y > canvas.height) this.y = 0;
        else if (this.y < 0) this.y = canvas.height;
      }

      draw(ctx) {
        // Draw glow
        ctx.shadowBlur = 15;
        ctx.shadowColor = this.color;
        ctx.fillStyle = this.color;
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fill();

        // Draw trail
        ctx.shadowBlur = 0;
        this.trail.forEach((pos, index) => {
          const alpha = (index + 1) / this.trail.length;
          ctx.fillStyle = `rgba(0, 184, 150, ${alpha * 0.2})`;
          ctx.beginPath();
          ctx.arc(pos.x, pos.y, this.size * (1 - index / this.trail.length), 0, Math.PI * 2);
          ctx.fill();
        });
      }
    }

    // Now we're using the Particle class and particleCount
    for (let i = 0; i < particleCount; i++) {
      particles.push(new Particle());
    }

    function getRandomNeonColor() {
      const neonColors = [
        '#FF00FF', // Magenta
        '#00FFFF', // Cyan
        '#FF00B8', // Hot Pink
        '#00FF00', // Lime
        '#FFA500', // Orange
        '#FF2D00', // Red Orange
        '#39FF14', // Neon Green
        '#F4F315'  // Neon Yellow
      ];
      return neonColors[Math.floor(Math.random() * neonColors.length)];
    }

    class MouseTrail {
      constructor(color) {
        this.points = [];
        this.maxLength = 50;
        this.baseColor = this.hexToHSL(color);
      }

      hexToHSL(hex) {
        let r = parseInt(hex.slice(1, 3), 16) / 255;
        let g = parseInt(hex.slice(3, 5), 16) / 255;
        let b = parseInt(hex.slice(5, 7), 16) / 255;

        let max = Math.max(r, g, b), min = Math.min(r, g, b);
        let h, s, l = (max + min) / 2;

        if (max === min) {
          h = s = 0;
        } else {
          let d = max - min;
          s = l > 0.5 ? d / (2 - max - min) : d / (max + min);
          switch (max) {
            case r: h = (g - b) / d + (g < b ? 6 : 0); break;
            case g: h = (b - r) / d + 2; break;
            case b: h = (r - g) / d + 4; break;
          }
          h /= 6;
        }

        return [h * 360, s * 100, l * 100];
      }

      addPoint(x, y) {
        this.points.unshift({ x, y, age: 0 });
        this.points.forEach(point => point.age++);
        if (this.points.length > this.maxLength) {
          this.points.pop();
        }
      }

      draw(ctx) {
        if (this.points.length < 2) return;

        ctx.lineCap = 'round';
        ctx.lineJoin = 'round';

        for (let i = 1; i < this.points.length; i++) {
          const start = this.points[i - 1];
          const end = this.points[i];

          const progress = i / this.points.length;
          const alpha = 1 - progress;
          const lineWidth = 20 * (1 - progress); // Start thick, end thin

          ctx.beginPath();
          ctx.moveTo(start.x, start.y);

          // Use quadratic curve for smoother lines
          if (i < this.points.length - 1) {
            const next = this.points[i + 1];
            const controlX = end.x;
            const controlY = end.y;
            const endX = (end.x + next.x) / 2;
            const endY = (end.y + next.y) / 2;
            ctx.quadraticCurveTo(controlX, controlY, endX, endY);
          } else {
            ctx.lineTo(end.x, end.y);
          }

          const gradient = ctx.createLinearGradient(start.x, start.y, end.x, end.y);
          gradient.addColorStop(0, `hsla(${this.baseColor[0]}, ${this.baseColor[1]}%, 50%, ${alpha})`);
          gradient.addColorStop(1, `hsla(${this.baseColor[0]}, ${this.baseColor[1]}%, 50%, ${alpha * 0.5})`);

          ctx.strokeStyle = gradient;
          ctx.lineWidth = lineWidth;
          ctx.stroke();
        }
      }
    }

    const mouseTrail = new MouseTrail('#00B896'); // Use your desired color here

    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.forEach(particle => {
        particle.update();
        particle.draw(ctx);
      });

      mouseTrail.draw(ctx);

      requestAnimationFrame(animate);
    }

    function handleMouseMove(event) {
      const rect = canvas.getBoundingClientRect();
      const x = event.clientX - rect.left;
      const y = event.clientY - rect.top;
      mouseTrail.addPoint(x, y);
    }

    canvas.addEventListener('mousemove', handleMouseMove);
    animate();

    // Add event listeners for info box
    const buttons = document.querySelectorAll('button');
    const infoBox = document.getElementById('info-box');

    buttons.forEach(button => {
      button.addEventListener('mouseenter', () => {
        infoBox.textContent = button.getAttribute('data-info');
        infoBox.classList.add('active');
      });

      button.addEventListener('mouseleave', () => {
        infoBox.classList.remove('active');
      });
    });

    // Clean up event listeners
    return () => {
      buttons.forEach(button => {
        button.removeEventListener('mouseenter', () => {});
        button.removeEventListener('mouseleave', () => {});
      });
      canvas.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <div className="App">
      <canvas ref={canvasRef}></canvas>
      <header>
        <div className="logo">Repute</div>
        <nav>
          <button data-info="Go to homepage">Home</button>
          <button data-info="Explore our features">Features</button>
          <button data-info="Check out our resources">Resources</button>
          <button data-info="View pricing options">Pricing</button>
          <button data-info="Get in touch with us">Contact</button>
          <button className="get-started" data-info="Start using Repute">Get started</button>
        </nav>
      </header>
      <main>
        <h1>
          <span className="heading-line">Boost Trust with</span>
          <span className="heading-line">AI-Crafted Testimonials</span>
        </h1>
        <p>Generate authentic, AI-crafted testimonials in seconds to boost trust, drive conversions, and elevate your brand's reputation.</p>
        <div className="cta-buttons">
          <button className="get-started" data-info="Start using Repute">Get started</button>
          <button className="features" data-info="Explore our features">Features</button>
        </div>
      </main>
      <div id="info-box" className="info-box"></div>
    </div>
  );
}

export default App;
