// This could be in a separate file, e.g., particles.js
export function setupParticles(canvas) {
    if (!canvas) {
        console.error('Canvas element not found');
        return;
    }

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const particles = [];

    class Particle {
      constructor() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.size = Math.random() * 3 + 1;
        this.speedX = Math.random() * 0.5 - 0.25;
        this.speedY = Math.random() * 0.5 - 0.25;
        this.color = getRandomNeonColor();
      }

      update() {
        this.x += this.speedX;
        this.y += this.speedY;

        if (this.x > canvas.width) this.x = 0;
        else if (this.x < 0) this.x = canvas.width;
        if (this.y > canvas.height) this.y = 0;
        else if (this.y < 0) this.y = canvas.height;
      }

      draw() {
        ctx.save(); // Save the current context state

        // Set glow effect
        ctx.shadowBlur = 15;
        ctx.shadowColor = this.color;

        // Draw the particle
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fillStyle = this.color;
        ctx.fill();

        ctx.restore(); // Restore the context state
      }
    }

    function init() {
      for (let i = 0; i < 50; i++) {
        particles.push(new Particle());
      }
    }

    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      particles.forEach(particle => {
        particle.update();
        particle.draw();
      });
      requestAnimationFrame(animate);
    }

    function resizeCanvas() {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    }

    init();
    animate();

    window.addEventListener('resize', resizeCanvas);

    console.log('Particles setup complete'); // Add this line for debugging

    return () => {
      window.removeEventListener('resize', resizeCanvas);
    };
  }

  function getRandomNeonColor() {
    const neonColors = ['#ff00ff', '#00ff00', '#00ffff', '#ff9900', '#ff0066', '#3300ff'];
    return neonColors[Math.floor(Math.random() * neonColors.length)];
  }

  // Call this function when your component mounts
  setupParticles();
  setupParticles();
