import React, { useEffect, useState, useRef } from 'react';
import './App.css';
import { setupParticles } from './particles';
import Logo from './components/Logo';

function App() {
  const [neonColor, setNeonColor] = useState(getRandomNeonColor());
  const canvasRef = useRef(null);

  function getRandomNeonColor() {
    const neonColors = [
      '#ff00ff', '#00ff00', '#00ffff', '#ff9900', '#ff0066', '#3300ff'
    ];
    return neonColors[Math.floor(Math.random() * neonColors.length)];
  }

  useEffect(() => {
    if (canvasRef.current) {
      setupParticles(canvasRef.current);
    }
    document.documentElement.style.setProperty('--neon-color', neonColor);
  }, [neonColor]);

  useEffect(() => {
    const generateColor = () => {
      const hue = Math.floor(Math.random() * 360);
      const color = `hsl(${hue}, 100%, 50%)`; // Original bright color
      const lighterColor = `hsl(${hue}, 100%, 35%)`; // Lighter version for text (increased from 30% to 35%)
      document.documentElement.style.setProperty('--neon-color', color);
      document.documentElement.style.setProperty('--neon-text-color', lighterColor);
    };

    generateColor();
    // If you want to change color periodically:
    // const interval = setInterval(generateColor, 5000);
    // return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <canvas ref={canvasRef} id="particleCanvas"></canvas>
      <header>
        <Logo />
        <nav>
          <button>Home</button>
          <button>Features</button>
          <button>Resources</button>
          <button>Pricing</button>
          <button>Contact</button>
          <button className="get-started">Get started</button>
        </nav>
      </header>
      <main>
        <section className="page page1">
          <div className="main-content">
            <h1>
              <span className="heading-line">Boost Trust with</span>
              <span className="heading-line neon-text">AI-Crafted Testimonials</span>
            </h1>
            <p className="subtitle">Generate authentic, AI-crafted testimonials in seconds to boost trust, drive conversions, and elevate your brand's reputation.</p>
            <div className="cta-buttons">
              <button className="get-started">Get started</button>
              <button className="features">Features</button>
            </div>
          </div>
        </section>
        <section className="page page2">
          <h2>About Repute</h2>
          <p>Learn more about our AI-powered testimonial generation...</p>
        </section>
        <section className="page page3">
          <h2>How It Works</h2>
          <p>Discover the process behind our authentic testimonials...</p>
        </section>
        <section className="page page4">
          <h2>Features</h2>
          <ul>
            <li>AI-powered testimonial generation</li>
            <li>Customizable templates</li>
            <li>Easy integration with your website</li>
            <li>Unlimited testimonials</li>
            <li>Real-time analytics</li>
            <li>24/7 customer support</li>
            <li>30-day money-back guarantee</li>
          </ul>
        </section>
        <section className="page page5">
          <h2>Benefits</h2>
          <ul>
            <li>Boost conversions and sales</li>
            <li>Improve credibility and trust</li>
            <li>Enhance customer satisfaction</li>
            <li>Save time and resources</li>
            <li>Gain a competitive edge</li>
          </ul>
        </section>
        <section className="page page6">
          <h2>Case Studies</h2>
          <div className="case-study">
            <h3>Company X</h3>
            <p>How Repute helped Company X increase conversions by Y%</p>
          </div>
          {/* Add more case studies */}
        </section>
        <section className="page page7">
          <h2>Pricing Plans</h2>
          <div className="pricing-grid">
            <div className="pricing-card">
              <h3>Basic</h3>
              <p>$X/month</p>
              <ul>
                <li>Feature 1</li>
                <li>Feature 2</li>
                <li>Feature 3</li>
              </ul>
            </div>
            {/* Add more pricing cards */}
          </div>
        </section>
        <section className="page page8">
          <h2>FAQs</h2>
          <div className="faq">
            <h3>Question 1</h3>
            <p>Answer 1</p>
          </div>
          {/* Add more FAQs */}
        </section>
        <section className="page page9">
          <h2>Resources</h2>
          <div className="resource">
            <h3>Resource 1</h3>
            <p>Description 1</p>
          </div>
          {/* Add more resources */}
        </section>
        <section className="page page10">
          <h2>Contact Us</h2>
          <p>Get in touch for more information...</p>
        </section>
      </main>
    </div>
  );
}

export default App;
