import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [neonColor, setNeonColor] = useState(getRandomNeonColor());
  const [reviews, setReviews] = useState([]);

  function getRandomNeonColor() {
    const neonColors = [
      '#ff00ff', '#00ff00', '#00ffff', '#ff9900', '#ff0066', '#3300ff'
    ];
    return neonColors[Math.floor(Math.random() * neonColors.length)];
  }

  function generateReview() {
    const names = ["John Doe", "Jane Smith", "Alex Johnson", "Sam Lee", "Terry Mendez", "Louisa Sullivan", "Jasmine Barnett", "Bobby Henderson"];
    const reviews = [
      "Amazing coffee at the cozy café! The baristas were passionate about their craft, and the place was perfect to relax and enjoy a cup of joe.",
      "Great quality and value! The T-shirts I bought from this brand are soft, durable, and affordable. They are now my go-to basics for everyday wear.",
      "Love these sunglasses! Stylish and provide excellent sun protection. A perfect accessory for any outfit.",
      "I had a fantastic time at the museum. The interactive exhibits made learning fun, and the staff's passion for sharing information was evident. It's a perfect place for a family outing."
    ];
    return {
      name: names[Math.floor(Math.random() * names.length)],
      text: reviews[Math.floor(Math.random() * reviews.length)],
      rating: Math.floor(Math.random() * 2) + 4,
      x: Math.random() * 80 + 10, // 10% to 90% of screen width
      y: Math.random() * 80 + 10, // 10% to 90% of screen height
    };
  }

  useEffect(() => {
    setReviews(Array(20).fill().map(generateReview));

    document.documentElement.style.setProperty('--neon-color', neonColor);

    const interval = setInterval(() => {
      setReviews(prevReviews => {
        const newReviews = [...prevReviews];
        const indexToReplace = Math.floor(Math.random() * newReviews.length);
        newReviews[indexToReplace] = generateReview();
        return newReviews;
      });
    }, 5000);

    return () => clearInterval(interval);
  }, [neonColor]);

  return (
    <div className="App">
      <div className="review-background">
        {reviews.map((review, index) => (
          <div key={index} className="review" style={{left: `${review.x}%`, top: `${review.y}%`}}>
            <div className="review-header">{review.name}</div>
            <div className="review-rating">{'★'.repeat(review.rating)}</div>
            <div className="review-text">{review.text.slice(0, 50)}...</div>
          </div>
        ))}
      </div>
      <header>
        <div className="logo animate-logo">Repute</div>
        <nav>
          <button>Home</button>
          <button>Features</button>
          <button>Resources</button>
          <button>Pricing</button>
          <button>Contact</button>
          <button className="get-started">Get started</button>
        </nav>
      </header>
      <main>
        <h1>
          <span className="heading-line">Boost Trust with</span>
          <span className="heading-line">AI-Crafted Testimonials</span>
        </h1>
        <p>Generate authentic, AI-crafted testimonials in seconds to boost trust, drive conversions, and elevate your brand's reputation.</p>
        <div className="cta-buttons">
          <button className="get-started">Get started</button>
          <button className="features">Features</button>
        </div>
      </main>
    </div>
  );
}

export default App;
