import React, { useEffect, useState, useRef } from 'react';
import './App.css';
import { setupParticles } from './particles';
import NeonButton from './components/NeonButton';

function App() {
  const [neonColor, setNeonColor] = useState(getRandomNeonColor());
  const canvasRef = useRef(null);

  function getRandomNeonColor() {
    const neonColors = [
      '#ff00ff', '#00ff00', '#00ffff', '#ff9900', '#ff0066', '#3300ff'
    ];
    return neonColors[Math.floor(Math.random() * neonColors.length)];
  }

  function darkenColor(color, amount) {
    return '#' + color.replace(/^#/, '').replace(/../g, color => ('0' + Math.max(0, Math.min(255, parseInt(color, 16) - amount)).toString(16)).substr(-2));
  }

  useEffect(() => {
    if (canvasRef.current) {
      setupParticles(canvasRef.current);
    }
    const darkerNeonColor = darkenColor(neonColor, 20);
    document.documentElement.style.setProperty('--neon-color', darkerNeonColor);
  }, [neonColor]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const particles = [];

    class Particle {
      constructor() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.size = Math.random() * 5 + 1;
        this.speedX = Math.random() * 3 - 1.5;
        this.speedY = Math.random() * 3 - 1.5;
        this.color = getRandomNeonColor();
      }

      update() {
        this.x += this.speedX;
        this.y += this.speedY;

        if (this.x > canvas.width) this.x = 0;
        else if (this.x < 0) this.x = canvas.width;
        if (this.y > canvas.height) this.y = 0;
        else if (this.y < 0) this.y = canvas.height;
      }

      draw() {
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fillStyle = this.color;
        ctx.fill();

        // Add glow effect
        ctx.shadowBlur = 15;
        ctx.shadowColor = this.color;
      }
    }

    function init() {
      for (let i = 0; i < 100; i++) {
        particles.push(new Particle());
      }
    }

    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      for (let i = 0; i < particles.length; i++) {
        particles[i].update();
        particles[i].draw();
      }
      requestAnimationFrame(animate);
    }

    init();
    animate();

    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div className="App">
      <canvas ref={canvasRef} id="particleCanvas"></canvas>
      <header>
        <a href="/" className="logo-link">
          <div className="logo animate-logo">Repute</div>
        </a>
        <nav>
          <NeonButton>Home</NeonButton>
          <NeonButton>Features</NeonButton>
          <NeonButton>Resources</NeonButton>
          <NeonButton>Pricing</NeonButton>
          <NeonButton>Contact</NeonButton>
          <button className="get-started">Get started</button>
        </nav>
      </header>
      <main>
        <section className="page page1">
          <div className="main-content">
            <h1>
              <span className="heading-line">Boost Trust with</span>
              <span className="heading-line neon-text">AI-Crafted Testimonials</span>
            </h1>
            <p className="subtitle">Generate authentic, AI-crafted testimonials in seconds to boost trust, drive conversions, and elevate your brand's reputation.</p>
            <div className="search-bar">
              <input type="text" placeholder="Search testimonials..." />
              <button>Search</button>
            </div>
            <div className="cta-buttons">
              <button className="get-started">Get started</button>
              <button className="features">Features</button>
            </div>
          </div>
        </section>
        <section className="page page2"></section>
        <section className="page page3"></section>
        <section className="page page4"></section>
        <section className="page page5"></section>
      </main>
      <footer style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100px'
      }}>
        <p style={{
          fontSize: 'calc(12px + 25px)',
          margin: 0
        }}>&copy; 2024 Repute. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
