import React, { useEffect, useState, useRef } from 'react';
import './App.css';
import { setupParticles } from './particles';
import NeonButton from './components/NeonButton';

function App() {
  const [neonColor, setNeonColor] = useState(getRandomNeonColor());
  const canvasRef = useRef(null);

  const neonColors = [
    '#FF3131', // Red
    '#3131FF', // Blue
    '#FF69B4', // Pink
    '#8A2BE2'  // Purple
  ];

  function getRandomNeonColor() {
    const randomIndex = Math.floor(Math.random() * neonColors.length);
    return neonColors[randomIndex];
  }

  function darkenColor(color, amount) {
    return '#' + color.replace(/^#/, '').replace(/../g, color => ('0' + Math.max(0, Math.min(255, parseInt(color, 16) - amount)).toString(16)).substr(-2));
  }

  useEffect(() => {
    if (canvasRef.current) {
      setupParticles(canvasRef.current);
    }
    const darkerNeonColor = darkenColor(neonColor, 20);
    document.documentElement.style.setProperty('--neon-color', darkerNeonColor);
  }, [neonColor]);

  useEffect(() => {
    setNeonColor();
    // If you want to change color periodically, you can set up an interval here
    // const interval = setInterval(setNeonColor, 5000); // Changes color every 5 seconds
    // return () => clearInterval(interval); // Clean up on component unmount
  }, []);

  return (
    <div className="App">
      <canvas ref={canvasRef} id="particleCanvas"></canvas>
      <header>
        <div className="logo animate-logo">Repute</div>
        <nav>
          <NeonButton>Home</NeonButton>
          <NeonButton>Features</NeonButton>
          <NeonButton>Resources</NeonButton>
          <NeonButton>Pricing</NeonButton>
          <button>Contact</button>
          <button className="get-started">Get started</button>
        </nav>
      </header>
      <main>
        <section className="page page1">
          <div className="main-content">
            <h1>
              <span className="heading-line">Boost Trust with</span>
              <span className="heading-line neon-text">AI-Crafted Testimonials</span>
            </h1>
            <p className="subtitle">Generate authentic, AI-crafted testimonials in seconds to boost trust, drive conversions, and elevate your brand's reputation.</p>
            <div className="cta-buttons">
              <button className="get-started">Get started</button>
              <button className="features">Features</button>
            </div>
          </div>
        </section>
        <section className="page page2"></section>
        <section className="page page3"></section>
        <section className="page page4"></section>
        <section className="page page5"></section>
      </main>
    </div>
  );
}

export default App;
