import React, { useState } from 'react';
import './Contact.css';

function Contact() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically send the data to a server
    console.log('Form submitted:', formData);
    // Clear form after submission
    setFormData({ name: '', email: '', message: '' });
  };

  return (
    <div className="contact-container">
  <div class="contact-info">
    <h1>Get in <span class="highlight">touch</span></h1>
    <p>If you have any questions, feel free to reach out.</p>
    <p>We do our best to reply within 48 hours.</p>

    <div class="email-info">
      <img src="email-icon.svg" alt="Email icon" className="email-icon" />
      <div>
        <p>Email:</p>
        <p class="highlight">support@repute.com</p>
      </div>
    </div>

    <h2>Follow us on social media</h2>
    <div class="social-icons">
      <a href="#" class="social-icon">f</a>
      <a href="#" class="social-icon">t</a>
      <a href="#" class="social-icon">ig</a>
      <a href="#" class="social-icon">in</a>
      <a href="#" class="social-icon">yt</a>
    </div>
  </div>

  <div class="contact-form">
    <form id="contactForm">
      <div class="form-group">
        <label htmlFor="name">Name</label>
        <input type="text" id="name" name="name" placeholder="John Carter" required />
      </div>
      <div class="form-group">
        <label htmlFor="email">Email</label>
        <input type="email" id="email" name="email" placeholder="example@email.com" required />
      </div>
      <div class="form-group">
        <label htmlFor="message">Message</label>
        <textarea id="message" name="message" placeholder="Your message here" required></textarea>
      </div>
      <button type="submit">Send</button>
    </form>
  </div>
</div>
  );
}

export default Contact;
