import React, { useEffect, useRef, useState } from 'react';
import './App.css';

function App() {
  const canvasRef = useRef(null);
  const [neonColor, setNeonColor] = useState(getRandomNeonColor());
  const [chatOpen, setChatOpen] = useState(false);
  const [inputMessage, setInputMessage] = useState('');
  const [messages, setMessages] = useState([]);

  function getRandomNeonColor() {
    const neonColors = [
      '#ff00ff', // Neon Pink
      '#00ff00', // Neon Green
      '#00ffff', // Neon Cyan
      '#ff9900', // Neon Orange
      '#ff0066', // Neon Rose
      '#3300ff'  // Neon Blue
    ];
    return neonColors[Math.floor(Math.random() * neonColors.length)];
  }

  const sendMessage = () => {
    if (inputMessage.trim() !== '') {
      setMessages([...messages, { text: inputMessage, sender: 'user' }]);
      setInputMessage('');
      // Here you would typically send the message to your backend
      // and get a response. For now, we'll just echo the message.
      setTimeout(() => {
        setMessages(msgs => [...msgs, { text: `You said: ${inputMessage}`, sender: 'bot' }]);
      }, 1000);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const particles = [];
    const particleCount = 100; // Now we'll use this

    for (let i = 0; i < particleCount; i++) {
      particles.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        radius: Math.random() * 2 + 1,
        color: neonColor,
        velocity: {
          x: (Math.random() - 0.5) * 0.5,
          y: (Math.random() - 0.5) * 0.5
        }
      });
    }

    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.forEach(particle => {
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.radius, 0, Math.PI * 2);
        ctx.fillStyle = particle.color;
        ctx.fill();

        // Add glow effect
        ctx.shadowBlur = 15;
        ctx.shadowColor = particle.color;

        // Update particle position
        particle.x += particle.velocity.x;
        particle.y += particle.velocity.y;

        // Wrap particles around the screen
        if (particle.x < 0) particle.x = canvas.width;
        if (particle.x > canvas.width) particle.x = 0;
        if (particle.y < 0) particle.y = canvas.height;
        if (particle.y > canvas.height) particle.y = 0;
      });

      requestAnimationFrame(animate);
    }

    animate();

    // ... rest of your useEffect code ...
  }, [neonColor]);

  return (
    <div className="App">
      <canvas ref={canvasRef}></canvas>
      <header>
        <div className="logo animate-logo">Repute</div>
        <nav>
          <button data-info="Go to homepage">Home</button>
          <button data-info="Explore our features">Features</button>
          <button data-info="Check out our resources">Resources</button>
          <button data-info="View pricing options">Pricing</button>
          <button data-info="Get in touch with us">Contact</button>
          <button className="get-started" data-info="Start using Repute">Get started</button>
        </nav>
      </header>
      <main>
        <h1>
          <span className="heading-line">Boost Trust with</span>
          <span className="heading-line">AI-Crafted Testimonials</span>
        </h1>
        <p>Generate authentic, AI-crafted testimonials in seconds to boost trust, drive conversions, and elevate your brand's reputation.</p>
        <div className="cta-buttons">
          <button className="get-started" data-info="Start using Repute">Get started</button>
          <button className="features" data-info="Explore our features">Features</button>
        </div>
      </main>
      <div id="info-box" className="info-box"></div>
      <div className="chatbot-container">
        <div className="chatbot-icon" onClick={() => setChatOpen(!chatOpen)}>
          {/* The icon is created entirely with CSS */}
        </div>
        {chatOpen && (
          <div className="chatbot-window">
            <div className="chatbot-header">
              <h3>Customer Support</h3>
              <button onClick={() => setChatOpen(false)}>Ã—</button>
            </div>
            <div className="chatbot-messages">
              {/* Messages will be displayed here */}
            </div>
            <div className="chatbot-input">
              <input 
                type="text" 
                placeholder="Type your message..."
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
              />
              <button onClick={sendMessage}>Send</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
