import React, { useEffect, useRef } from 'react';
import './App.css';

const App = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Set white background
    ctx.fillStyle = '#FFFFFF';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    const particles = [];
    const particleCount = 50; // Adjust this number as needed

    function getRandomNeonColor() {
      const neonColors = [
        '#FF00FF', // Magenta
        '#00FFFF', // Cyan
        '#FF00B8', // Hot Pink
        '#00FF00', // Lime
        '#FFA500', // Orange
        '#FF2D00', // Red Orange
        '#39FF14', // Neon Green
        '#F4F315'  // Neon Yellow
      ];
      return neonColors[Math.floor(Math.random() * neonColors.length)];
    }

    class Particle {
      constructor() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.size = Math.random() * 3 + 1;
        this.speedX = Math.random() * 3 - 1.5;
        this.speedY = Math.random() * 3 - 1.5;
        this.color = '#00B896';
        this.trail = [];
        this.trailLength = 5;
      }

      update() {
        this.trail.unshift({ x: this.x, y: this.y });
        if (this.trail.length > this.trailLength) {
          this.trail.pop();
        }

        this.x += this.speedX;
        this.y += this.speedY;

        if (this.x > canvas.width) this.x = 0;
        else if (this.x < 0) this.x = canvas.width;
        if (this.y > canvas.height) this.y = 0;
        else if (this.y < 0) this.y = canvas.height;
      }

      draw(ctx) {
        // Draw glow
        ctx.shadowBlur = 15;
        ctx.shadowColor = this.color;
        ctx.fillStyle = this.color;
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fill();

        // Draw trail
        ctx.shadowBlur = 0;
        this.trail.forEach((pos, index) => {
          const alpha = (index + 1) / this.trail.length;
          ctx.fillStyle = `rgba(0, 184, 150, ${alpha * 0.2})`;
          ctx.beginPath();
          ctx.arc(pos.x, pos.y, this.size * (1 - index / this.trail.length), 0, Math.PI * 2);
          ctx.fill();
        });
      }
    }

    function createParticles() {
      for (let i = 0; i < particleCount; i++) {
        particles.push(new Particle());
      }
    }

    function animateParticles() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.forEach(particle => {
        particle.update();
        particle.draw(ctx);
      });

      requestAnimationFrame(animateParticles);
    }

    createParticles();
    animateParticles();

    return () => {
      // Cleanup if needed
    };
  }, []);

  return (
    <div className="App">
      <canvas ref={canvasRef}></canvas>
      <header>
        <nav>
          <button>About</button>
          <button>Pricing</button>
          <button>Contact</button>
          <button>Login</button>
        </nav>
      </header>
      <main>
        <h1 className="brand-name">Repute</h1>
      </main>
    </div>
  );
};

export default App;
