import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navigation.css'; // Make sure to create this CSS file

function Navigation() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav>
      <ul>
        <li><Link to="/">Home</Link></li>
        <li className="dropdown">
          <Link to="/features">Features</Link>
          <button onClick={toggleDropdown} className="dropdown-toggle">â–¼</button>
          {isOpen && (
            <div className="dropdown-content">
              {/* Your dropdown menu items */}
            </div>
          )}
        </li>
        <li><Link to="/pricing">Pricing</Link></li>
        <li><Link to="/resources">Resources</Link></li>
        <li><Link to="/contact">Contact</Link></li>
        <li><Link to="/signup" className="get-started">Get Started</Link></li>
      </ul>
    </nav>
  );
}

export default Navigation;
