import React, { useState, useEffect } from 'react';
import './InteractiveTestimonial.css';

const portraits = [
  '/path/to/portrait1.jpg',
  '/path/to/portrait2.jpg',
  '/path/to/portrait3.jpg',
  // Add more image paths
];

const testimonials = [
  "This product has transformed my business!",
  "Amazing service and fantastic results.",
  "I couldn't be happier with the outcomes.",
  // Add more testimonial texts
];

const starRatings = [3, 4, 5];

const InteractiveTestimonial = () => {
  const [portrait, setPortrait] = useState('');
  const [testimonial, setTestimonial] = useState('');
  const [rating, setRating] = useState(0);
  const [isGenerating, setIsGenerating] = useState(false);

  const generateTestimonial = () => {
    setIsGenerating(true);

    // Generate portrait
    let portraitInterval = setInterval(() => {
      setPortrait(portraits[Math.floor(Math.random() * portraits.length)]);
    }, 100);

    setTimeout(() => {
      clearInterval(portraitInterval);
      setPortrait(portraits[Math.floor(Math.random() * portraits.length)]);
    }, 1500);

    // Generate testimonial text
    setTimeout(() => {
      let testimonialInterval = setInterval(() => {
        setTestimonial(testimonials[Math.floor(Math.random() * testimonials.length)]);
      }, 100);

      setTimeout(() => {
        clearInterval(testimonialInterval);
        setTestimonial(testimonials[Math.floor(Math.random() * testimonials.length)]);
      }, 1500);
    }, 1000);

    // Generate star rating
    setTimeout(() => {
      let ratingInterval = setInterval(() => {
        setRating(starRatings[Math.floor(Math.random() * starRatings.length)]);
      }, 100);

      setTimeout(() => {
        clearInterval(ratingInterval);
        setRating(starRatings[Math.floor(Math.random() * starRatings.length)]);
        setIsGenerating(false);
      }, 1500);
    }, 2000);
  };

  const generateStars = (rating) => {
    return '⭐'.repeat(rating) + '☆'.repeat(5 - rating);
  };

  return (
    <div className="interactive-testimonial">
      <button onClick={generateTestimonial} disabled={isGenerating}>
        Generate Testimonial
      </button>
      <div className={`testimonial-container ${isGenerating ? 'generating' : ''}`}>
        <div className="portrait" style={{backgroundImage: `url(${portrait})`}}></div>
        <div className="testimonial-text">{testimonial}</div>
        <div className="star-rating">{generateStars(rating)}</div>
      </div>
    </div>
  );
};

export default InteractiveTestimonial;
